int currFloor = 0;
int position = 0;
for (int i = 0; i < input.Length; i++)
{
    if (input[i].Equals('('))
    {
        currFloor++;
    }
    else
    {
        currFloor--;
    }

    if (currFloor < 0)
    {
        position = i;
        break;
    }
}

int answerDay2 = position + 1; // position is 1 based rather than 0 based

Console.WriteLine(answerDay2);
